$(function(){$(".selector").on("change",function(){const t=$(this).val();""!==t&&($(".loader").show(),$(".articles").empty(),$.ajax({method:"GET",dataType:"json",url:"https://api.nytimes.com/svc/topstories/v2/"+t+".json?api-key=N8PTNVMsydAsVz1zZpGLuJV22NbJOlQB"}).done(function(t){const e=t.results.filter(function(t){return t.multimedia.length}).slice(0,11);$.each(e,function(t,e){$(".articles").append('<li><a href="'+e.url+'" target="_blank"><div class="article_box" style="background-image:url('+e.multimedia[4].url+')">'+e.title+"</li>")})}).fail(function(){$.alert("Something went wrong. Please try again.")}).always(function(){$(".loader").hide()}))})});